#include<iostream>#include<string>using namespace std;class PersonInfo{	private:		string name;		int year;	public:		PersonInfo(string name, int year);		~PersonInfo();		string Name();		int Age();};PersonInfo::PersonInfo(string name, int year){	this->name = name;	this->year = year;	 }PersonInfo::~PersonInfo(){	}string PersonInfo::Name(){	return name;}int PersonInfo::Age(){	return year;}int main(){    string    name;    int    year;    cin >> name >> year;    PersonInfo info(name, year);    cout << "I am " << info.Name() << ", " << info.Age() << " years old.\n";    return 0;}