#include<iostream>#include<string>using namespace std;class Animal{	public:	virtual void Print()=0;};class Fish:public Animal{	private:		string type, color;    	bool Osteichthyes;    public:    	Fish(string t,string c,bool o){    		type = t;    		color = c;    		Osteichthyes = o;		}		void Print(){			cout<<"type:"<<type<<", color:"<<color<<", Osteichthyes:"<<Osteichthyes<<endl;		}};class Bird:public Animal{	private:		string type, color;    	bool daytime;    public:    	Bird(string t,string c,bool d){    		type = t;    		color = c;    		daytime = d;		}		void Print(){			cout<<"type:"<<type<<", color:"<<color<<", daytime:"<<daytime<<endl;		}};int main(){    Animal *animal;    string type, color;    bool Osteichthyes, daytime;    cin >> type >> color >> Osteichthyes;    Fish fish(type, color, Osteichthyes);    fish.Print();    animal = &fish;    animal->Print();    cin >> type >> color >> daytime;    Bird bird(type, color, daytime);    bird.Print();    animal = &bird;    animal->Print();    return 0;}